package main

import "log"

func main() {
	input := ""

	log.Println(part1(input))
	log.Println(part2(input))
}

func part1(input string) int {
	return getMarkerPos(input, 4)
}

func part2(input string) int {
	return getMarkerPos(input, 14)
}

func getMarkerPos(input string, numChars int) int {
	for i := numChars; i < len(input); i++ {
		part := input[i-numChars : i]
		if isUnique(part) {
			return i
		}
	}

	return 0
}

func isUnique(rs string) bool {
	for i := 0; i < len(rs); i++ {
		for j := i + 1; j < len(rs); j++ {
			if rs[i] == rs[j] {
				return false
			}
		}
	}

	return true
}
